const sseString = $json.data;

// SSE (Server-Sent Events) is a one-way communication method where the server continuously 
// sends data updates to the client over an HTTP connection. The client receives these 
// updates as a stream of text-based events.

// Function to extract JSON data from an SSE message
function parseSSE(sse) {
  // Split the SSE string into individual lines
  const lines = sse.split('\n');

  // Find the line that starts with 'data: '
  const dataLine = lines.find(line => line.startsWith('data: '));

  // If no data line is found, return null
  if (!dataLine) return null;

  // Remove the 'data: ' prefix and try to parse the rest as JSON
  const jsonString = dataLine.slice('data: '.length);

  try {
    return JSON.parse(jsonString);
  } catch (err) {
    console.error('Error while parsing:', err);
    return null;
  }
}

// Save parsed SSE data into the output object
$input.item.json.sseData = parseSSE(sseString);

// Remove the already parsed source data
delete $json.data;

return $input.item;

/*
  Returns a formatted datetime string showing the weekday name,
  ISO-style date, 24-hour time, and timezone.
  Format: dddd, YYYY-MM-DD HH:mm:ss (TIMEZONE)

  Parameters:
    timeZone - (optional) IANA timezone string, e.g., "Europe/Berlin".
    locale   - (optional) Locale string for formatting, e.g., "en-US".

  Examples:
    getDate() -> "Saturday, 2025-11-22 15:30:00 (Europe/Berlin)"
    getDate("UTC", "en-US") -> "Saturday, 2025-11-22 14:30:00 (UTC)"
*/
function getFormattedDateTime(timeZone = "Europe/Berlin", locale = "en-US") {
  const date = new Date();

  const year = date.toLocaleString(locale, { year: "numeric", timeZone });
  const month = date.toLocaleString(locale, { month: "2-digit", timeZone });
  const day = date.toLocaleString(locale, { day: "2-digit", timeZone });
  const weekday = date.toLocaleString(locale, { weekday: "long", timeZone });

  const time = date.toLocaleTimeString(locale, {
    hour12: false,
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
    timeZone
  });

  return `${weekday}, ${year}-${month}-${day} ${time} (${timeZone})`;
}

return { "formattedDateTime" : getFormattedDateTime(timeZone = $json.timeZone, locale = $json.locale) };

